INSERT INTO tarot_cards (name, name_bg, card_type, image_url, upright_meaning) VALUES
('The Fool', 'Лудият', 'major_arcana', '/tarot/00-fool.jpg', 'Ново начало, спонтанност, вяра в живота. Време е да направиш скока и да се довериш на вселената.'),
('The Magician', 'Магьосникът', 'major_arcana', '/tarot/01-magician.jpg', 'Сила на волята, проявяване, действие. Имаш всички инструменти, за да постигнеш целите си.'),
('The High Priestess', 'Върховната Жрица', 'major_arcana', '/tarot/02-high-priestess.jpg', 'Интуиция, тайно знание, вътрешен глас. Слушай сърцето си и довери се на вътрешната мъдрост.'),
('The Empress', 'Императрицата', 'major_arcana', '/tarot/03-empress.jpg', 'Изобилие, плодородие, грижа. Време е за творчество и създаване на красота.'),
('The Emperor', 'Императорът', 'major_arcana', '/tarot/04-emperor.jpg', 'Структура, авторитет, контрол. Нужна ти е дисциплина и яс ни граници.'),
('The Hierophant', 'Йерофантът', 'major_arcana', '/tarot/05-hierophant.jpg', 'Традиция, духовно учение, конформизъм. Потърси мъдрост в проверените пътища.'),
('The Lovers', 'Влюбените', 'major_arcana', '/tarot/06-lovers.jpg', 'Любов, хармония, избор. Важен избор предстои - слушай сърцето си.'),
('The Chariot', 'Колесницата', 'major_arcana', '/tarot/07-chariot.jpg', 'Воля, решителност, победа. Напред към целта с увереност и сила!'),
('Strength', 'Силата', 'major_arcana', '/tarot/08-strength.jpg', 'Вътрешна сила, смелост, търпение. Истинската сила идва от състрадание и разбиране.'),
('The Hermit', 'Отшелникът', 'major_arcana', '/tarot/09-hermit.jpg', 'Вътрешно търсене, самота, мъдрост. Време е за рефлексия и себепознание.'),
('Wheel of Fortune', 'Колелото на Съдбата', 'major_arcana', '/tarot/10-wheel.jpg', 'Промяна, цикли, съдба. Животът се върти - приеми промените.'),
('Justice', 'Справедливостта', 'major_arcana', '/tarot/11-justice.jpg', 'Баланс, истина, справедливост. Всяко действие има последствия.'),
('The Hanged Man', 'Обесеният', 'major_arcana', '/tarot/12-hanged-man.jpg', 'Нова перспектива, жертва, изчакване. Виж нещата от друг ъгъл.'),
('Death', 'Смъртта', 'major_arcana', '/tarot/13-death.jpg', 'Трансформация, край на цикъл, прераждане. Старото умира, за да роди ново.'),
('Temperance', 'Въздържанието', 'major_arcana', '/tarot/14-temperance.jpg', 'Баланс, модерация, хармония. Намери средата между крайностите.'),
('The Devil', 'Дяволът', 'major_arcana', '/tarot/15-devil.jpg', 'Изкушение, зависимост, материализъм. Внимавай да не се заробиш на илюзии.'),
('The Tower', 'Кулата', 'major_arcana', '/tarot/16-tower.jpg', 'Внезапна промяна, разрушение, прозрение. Понякога старото трябва да падне.'),
('The Star', 'Звездата', 'major_arcana', '/tarot/17-star.jpg', 'Надежда, вдъхновение, обновление. След бурята идва светлина.'),
('The Moon', 'Луната', 'major_arcana', '/tarot/18-moon.jpg', 'Илюзии, страхове, интуиция. Не всичко е такова, каквото изглежда.'),
('The Sun', 'Слънцето', 'major_arcana', '/tarot/19-sun.jpg', 'Радост, успех, позитивност. Животът свети ярко за теб!'),
('Judgement', 'Съдът', 'major_arcana', '/tarot/20-judgement.jpg', 'Прераждане, оценка, призвание. Време е за важен избор и себеоценка.'),
('The World', 'Светът', 'major_arcana', '/tarot/21-world.jpg', 'Завършване, цялостност, постижение. Постигна важна цел - празнувай!');
